import React, { useRef, useEffect } from 'react';
import YouTube from 'react-youtube';
import { Box, Button, Typography, Container, Paper, Grid } from '@mui/material';

const VideoPlayer = ({ videoId, title, onComplete, goToNextStep, goToPreviousStep, goToTop }) => {
  const playerRef = useRef(null);

  const handleVideoEnd = () => {
    onComplete();
  };

  useEffect(() => {
    // Ensure the playerRef is ready and videoId changes
    if (playerRef.current) {
      playerRef.current.internalPlayer.loadVideoById(videoId);
    }
  }, [videoId]);

  return (
    <Container maxWidth="md" sx={{ mt: 4 }}>
      <Paper sx={{ p: 3 }}>
        <Typography variant="h5" gutterBottom>{title}</Typography>
        <YouTube
          videoId={videoId}
          ref={playerRef}
          opts={{ width: '100%', playerVars: { autoplay: 1 } }}
          onEnd={handleVideoEnd}
        />
        <Grid container spacing={2} justifyContent="center" sx={{ mt: 2 }}>
          <Grid item>
            <Button onClick={goToPreviousStep} variant="contained" color="primary">
              戻る
            </Button>
          </Grid>
          <Grid item>
            <Button onClick={goToNextStep} variant="contained" color="primary">
              次へ
            </Button>
          </Grid>
          <Grid item>
            <Button onClick={goToTop} variant="contained" color="primary">
              トップに戻る
            </Button>
          </Grid>
        </Grid>
      </Paper>
    </Container>
  );
};

export default VideoPlayer;
