import React, { useState } from 'react';
import { Box, Button, Radio, RadioGroup, FormControlLabel, Typography, Container, Paper, Grid } from '@mui/material';

const Survey = ({ questions, onComplete, goToNextStep, goToPreviousStep, isLastStep, goToTop }) => {
  const [answers, setAnswers] = useState(Array(questions.length).fill(''));

  const handleAnswerChange = (questionIndex, answer) => {
    const newAnswers = [...answers];
    newAnswers[questionIndex] = answer;
    setAnswers(newAnswers);
  };

  const handleSubmit = () => {
    onComplete(answers);
  };

  return (
    <Container maxWidth="md" sx={{ mt: 4 }}>
      <Paper sx={{ p: 3 }}>
        <Typography variant="h5" gutterBottom>アンケート</Typography>
        {questions.map((question, index) => (
          <Box key={index} sx={{ mb: 2 }}>
            <Typography variant="body1">{question.text}</Typography>
            <RadioGroup
              value={answers[index]}
              onChange={(e) => handleAnswerChange(index, e.target.value)}
              sx={{ mt: 1 }}
            >
              {question.options.map((option, optionIndex) => (
                <FormControlLabel
                  key={optionIndex}
                  value={option}
                  control={<Radio />}
                  label={option}
                />
              ))}
            </RadioGroup>
          </Box>
        ))}
        <Grid container spacing={2} justifyContent="center">
          <Grid item>
            <Button onClick={goToPreviousStep} variant="contained" color="primary">
              戻る
            </Button>
          </Grid>
          <Grid item>
            <Button onClick={handleSubmit} variant="contained" color="primary">
              {isLastStep ? '完了' : '次へ'}
            </Button>
          </Grid>
          <Grid item>
            <Button onClick={goToTop} variant="contained" color="primary">
              トップに戻る
            </Button>
          </Grid>
        </Grid>
      </Paper>
    </Container>
  );
};

export default Survey;
