import React, { useState } from 'react';
import { Dialog, DialogTitle, DialogContent, DialogActions, Button, TextField, IconButton, Box, Typography, Grid } from '@mui/material';
import { Add as AddIcon, Delete as DeleteIcon, DragIndicator as DragIndicatorIcon } from '@mui/icons-material';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const QuestionEditor = ({ initialQuestions, onSave, onCancel }) => {
  const [questions, setQuestions] = useState(initialQuestions);

  const handleAddQuestion = () => {
    setQuestions([...questions, { type: 'multiple-choice', text: '', options: ['', '', '', '', ''] }]);
  };

  const handleQuestionChange = (index, key, value) => {
    const newQuestions = [...questions];
    newQuestions[index][key] = value;
    setQuestions(newQuestions);
  };

  const handleOptionChange = (questionIndex, optionIndex, value) => {
    const newQuestions = [...questions];
    newQuestions[questionIndex].options[optionIndex] = value;
    setQuestions(newQuestions);
  };

  const handleRemoveQuestion = (index) => {
    const newQuestions = [...questions];
    newQuestions.splice(index, 1);
    setQuestions(newQuestions);
  };

  const handleDragEnd = (result) => {
    if (!result.destination) return;
    const items = Array.from(questions);
    const [reorderedItem] = items.splice(result.source.index, 1);
    items.splice(result.destination.index, 0, reorderedItem);
    setQuestions(items);
  };

  return (
    <Dialog open onClose={onCancel} fullWidth maxWidth="md">
      <DialogTitle>質問を編集</DialogTitle>
      <DialogContent>
        <DragDropContext onDragEnd={handleDragEnd}>
          <Droppable droppableId="questions">
            {(provided) => (
              <Box {...provided.droppableProps} ref={provided.innerRef}>
                {questions.map((question, index) => (
                  <Draggable key={index} draggableId={`question-${index}`} index={index}>
                    {(provided) => (
                      <Box
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        sx={{ mb: 3, p: 2, border: '1px solid #ccc', borderRadius: 2, backgroundColor: '#f9f9f9' }}
                      >
                        <Grid container alignItems="center" spacing={1}>
                          <Grid item>
                            <IconButton {...provided.dragHandleProps}>
                              <DragIndicatorIcon />
                            </IconButton>
                          </Grid>
                          <Grid item xs>
                            <TextField
                              label={`質問 ${index + 1}`}
                              variant="outlined"
                              fullWidth
                              value={question.text}
                              onChange={(e) => handleQuestionChange(index, 'text', e.target.value)}
                              placeholder="質問内容を入力してください"
                            />
                          </Grid>
                          <Grid item>
                            <IconButton onClick={() => handleRemoveQuestion(index)}>
                              <DeleteIcon />
                            </IconButton>
                          </Grid>
                        </Grid>
                        <Box sx={{ mt: 2 }}>
                          <Typography variant="body1">選択肢:</Typography>
                          {question.options.map((option, optionIndex) => (
                            <TextField
                              key={optionIndex}
                              variant="outlined"
                              fullWidth
                              value={option}
                              onChange={(e) => handleOptionChange(index, optionIndex, e.target.value)}
                              sx={{ mt: 1 }}
                              placeholder="選択肢を入力してください"
                            />
                          ))}
                        </Box>
                      </Box>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </Box>
            )}
          </Droppable>
        </DragDropContext>
        <Button startIcon={<AddIcon />} onClick={handleAddQuestion} sx={{ mt: 2 }}>
          質問を追加
        </Button>
      </DialogContent>
      <DialogActions>
        <Button onClick={onCancel}>キャンセル</Button>
        <Button onClick={() => onSave(questions)} variant="contained" color="primary">
          保存
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default QuestionEditor;
